mapper.select:
SELECT id, user_name as userName, user_password as userPassword, user_email as userEmail, user_info as userInfo, head_img as headImg, create_time as createTime FROM sys_user WHERE 1 = 1
AND user_name like concat('%', #{userName}, '%')
AND user_email = #{userEmail}

SELECT id, user_name as userName, user_password as userPassword, user_email as userEmail, user_info as userInfo, head_img as headImg, create_time as createTime FROM sys_user WHERE 1 = 1
AND id = #{id}
AND user_name = #{userName}
AND 1 = 2
